package com.yunke.aioutbound.view.aiimport android.view.LayoutInflaterimport android.view.ViewGroupimport com.yunke.aioutbound.databinding.ItemAiRecordBindingimport com.yunke.module_base.view.adapter.BaseAdapter/** * @author Linker * @description: ai录音适配器 * @date :2024/11/1 */class AiRecordAdapter : BaseAdapter<String>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BaseViewHolder<String> =        ViewHolder(parent, ItemAiRecordBinding.inflate(LayoutInflater.from(parent.context), parent, false))    /**     * 数据绑定     */    class ViewHolder(val parent: ViewGroup, val viewBinding: ItemAiRecordBinding) : BaseViewHolder<String>(viewBinding) {        override fun bindData(data: String, position: Int) {            viewBinding.tvTitle.text = data        }    }}