package com.yunke.aioutbound.view.main.appimport android.view.LayoutInflaterimport android.view.ViewGroupimport com.yunke.aioutbound.databinding.ItemAppBindingimport com.yunke.lib_common.recycler.SpaceItemDecorationimport com.yunke.lib_common.util.DisplayUtilimport com.yunke.module_base.view.adapter.BaseAdapter/** * @author Linker * @description: * @date :2024/11/1 */class AppAdapter : BaseAdapter<String>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BaseViewHolder<String> =        ViewHolder(parent, ItemAppBinding.inflate(LayoutInflater.from(parent.context), parent, false))    class ViewHolder(val parent: ViewGroup, val viewBinding: ItemAppBinding) : BaseViewHolder<String>(viewBinding) {        val mList = mutableListOf<String>()        init {            val top = DisplayUtil.dip2px(parent.context, 10f)            viewBinding.recyclerView.addItemDecoration(SpaceItemDecoration(top, 0, top, 0))        }        override fun bindData(data: String, position: Int) {            viewBinding.tvTitle.text = data            val mAdapter = AppListAdapter()            mList.add("日程")            mList.add("双向外呼")            mList.add("号码清洗")            mList.add("闪信服务")            mList.add("SIP外呼")            mList.add("防封电销卡")            mAdapter.setList(mList)            viewBinding.recyclerView.adapter = mAdapter        }    }}