package com.yunke.aioutbound.view.main.appimport androidx.recyclerview.widget.RecyclerViewimport com.yunke.aioutbound.databinding.FragmentAppBindingimport com.yunke.lib_common.recycler.SpaceItemDecorationimport com.yunke.lib_common.util.DisplayUtilimport com.yunke.module_base.view.adapter.BaseAdapterimport com.yunke.module_base.view.fragment.BaseListFragmentimport com.yunke.module_base.view.fragment.ViewModelFragment/** * @author Linker * @description: 应用 * @date :2024/11/1 */class AppFragment : BaseListFragment<FragmentAppBinding, AppViewModel, String>() {    override fun getRecyclerView(): RecyclerView? = viewBinding()?.recyclerView    override fun getBaseAdapter(): BaseAdapter<String> = AppAdapter()    override fun loadData() {    }    override fun initData() {        super.initData()        val space = DisplayUtil.dip2px(mContext, 10f)        viewBinding()?.recyclerView?.addItemDecoration(SpaceItemDecoration(0, space, space, space))        mAdapter?.addData("外呼系统")        mAdapter?.addData("工具应用")        mAdapter?.addData("常用应用")        mAdapter?.addData("工具应用")    }    override fun setListener() {    }}