package com.yunke.aioutbound.view.main.appimport android.view.LayoutInflaterimport android.view.ViewGroupimport com.yunke.aioutbound.databinding.ItemAppListBindingimport com.yunke.module_base.view.adapter.BaseAdapter/** * @author Linker * @description: * @date :2024/11/1 */class AppListAdapter : BaseAdapter<String>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BaseViewHolder<String> =        ViewHolder(parent, ItemAppListBinding.inflate(LayoutInflater.from(parent.context), parent, false))    class ViewHolder(val parent: ViewGroup, val viewBinding: ItemAppListBinding) : BaseViewHolder<String>(viewBinding) {        override fun bindData(data: String, position: Int) {            viewBinding.tvName.text = data        }    }}