package com.yunke.aioutbound.view.main.homeimport android.content.Contextimport android.net.Uriimport android.os.Bundleimport android.util.Logimport androidx.lifecycle.viewModelScopeimport com.yunke.lib_common.BaseApplicationimport com.yunke.module_base.vm.BaseViewModelimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launch/** * @author Linker * @description: * @date :2024/11/1 */class HomeViewModel : BaseViewModel() {    //    static//    private val AUTHORITY = "content://com.yunke.enterprisep.service.provider.EnterpriseProvider"    private val AUTHORITY = "content://com.yunke.enterprisep.service.provider.EnterpriseProvider"    fun getImei(context: Context) {        viewModelScope.launch(Dispatchers.IO) {            runCatching {                Log.i(TAG, "tezst   11111111111")                val uri = Uri.parse(AUTHORITY)                val extras = Bundle()                extras.putInt("slot_id", 1)                val mBundle = context.contentResolver.call(uri, "getImei", null, null)                Log.i(TAG, "tezst   22222222")                val mImei = mBundle?.getString("imei", "3333")                Log.i(TAG, "tezst   ${mImei}")            }.onFailure {                Log.e(TAG, "tezst   ${it.toString()}")            }        }    }    fun setPhoneNumberMask(context: Context) {        runCatching {            Log.i(TAG, "tezst   444444")//            val packageNames = arrayListOf<String>()//            packageNames.add("com.android.incallui")//电话//            packageNames.add("com.android.contacts")//通讯录//            packageNames.add("com.android.mms")//短信//            packageNames.add("com.miui.huanji")//一建换机            val extras = Bundle()            extras.putBoolean("isHidePhone", false)//是否开启            context.contentResolver.call(Uri.parse(AUTHORITY), "setPhoneNumberHideApps", null, extras)            Log.i(TAG, "tezst   5555555")        }.onFailure {            Log.e(TAG, "tezst   ${it.toString()}")        }    }}